SELECT job.job_title AS 'Cargo', ROUND(AVG(employe.salary), 2) AS 'Média salarial', CASE WHEN (AVG(employe.salary) >= 2000 and AVG(employe.salary) <= 5800) THEN 'Júnior' WHEN (AVG(employe.salary) >= 5800 and AVG(employe.salary) <= 7500) THEN 'Pleno' WHEN (AVG(employe.salary) >= 7500 and AVG(employe.salary) <= 10500) THEN 'Sênior' ELSE 'CEO' END AS 'Senioridade' FROM hr.employees AS employe JOIN hr.jobs AS job ON employe.job_id = job.job_id ORDER BY AVG(employe.salary);